// Main Home Page
const Home = () => {
  const { t } = useTranslation();
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [dashboardData, setDashboardData] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await dashboardAPI.getHome();
        setDashboardData(response.data);
      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  return (
    <Box>
      <Grid container spacing={3}>
        {/* Left Column (60%) */}
        <Grid item xs={12} lg={7}>
          {/* Important Announcements */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6" fontWeight={700} color="primary">
                  {t('home.announcements')}
                </Typography>
                <Button size="small" href="/announcements">
                  {t('common.viewMore')}
                </Button>
              </Box>
              <AnnouncementList announcements={dashboardData?.announcements} loading={loading} />
            </CardContent>
          </Card>

          {/* Ongoing Programs */}
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6" fontWeight={700}>
                  {t('home.ongoingPrograms')}
                </Typography>
                <Button size="small" href="/programs">
                  {t('common.viewMore')}
                </Button>
              </Box>
              <OngoingPrograms programs={dashboardData?.programs} loading={loading} />
            </CardContent>
          </Card>
        </Grid>

        {/* Right Column (40%) */}
        <Grid item xs={12} lg={5}>
          {/* My Status */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" fontWeight={700} sx={{ mb: 2 }}>
                {t('home.myStatus')}
              </Typography>
              <MyStatus stats={dashboardData?.stats} loading={loading} />
            </CardContent>
          </Card>

          {/* Monthly Calendar */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" fontWeight={700} sx={{ mb: 2 }}>
                {t('home.monthlyCalendar')}
              </Typography>
              <CalendarWidget events={dashboardData?.events} />
            </CardContent>
          </Card>

          {/* Job Recommendations */}
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6" fontWeight={700}>
                  {t('home.jobRecommendations')}
                </Typography>
                <Button size="small" href="/jobs">
                  {t('common.viewMore')}
                </Button>
              </Box>
              <JobRecommendations jobs={dashboardData?.jobs} loading={loading} />
            </CardContent>
          </Card>
        </Grid>

        {/* Quick Access */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight={700} sx={{ mb: 2 }}>
                {t('home.quickAccess')}
              </Typography>
              <QuickAccess />
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Home;
